async function getFromCache(asin) {
 return new Promise((resolve) => {
 chrome.storage.local.get([asin], (result) => {
 const data = result[asin];
 if (data && Date.now() - data.timestamp < 12 \* 60 \* 60 \* 1000) {
 resolve(data.results);
 } else {
 resolve(null);
 }
 });
 });
}

function saveToCache(asin, results) {
 const data = {
 timestamp: Date.now(),
 results,
 };
 chrome.storage.local.set({ [asin]: data });
}

